}


.noscroll {
  overflow: hidden;
}



/* Root element (use instead of `body`) */
/* body#page {
  zoom: var(--zoom);
} */
#page {
  /* zoom: var(--zoom); */
  scroll-behavior: smooth;
  scroll-padding: 6em;
  padding: 0;
  margin: 0;
  background-color: white;
  font-size: 16px;


  color: var(--color);


  font-size: 16px;
  font-family: var(--font1);
  font-weight: 300;

  a {
    text-decoration: none;
  }

  @media (hover: hover) and (pointer: fine) {
    a:hover {
      text-decoration: none;
    }
  }
}

/* Elements */
.section {
  background-color: white;
}

.section-container {
  max-width: 1200px;
  margin: 0 auto;
  width: auto;
  padding-bottom: 3rem;

  @media screen and (min-width: 768px) {
    width: calc(100% - 2rem);
  }
  &.no-bottom-padding {
    padding-bottom: 0;
  }
}

a.link {
  line-height: 1.3;

  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px);
  /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: 2.5rem;
  line-height: 1.15;

}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 0;
  padding: 18px 24px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0;
  /* reset */

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

