section {
	background: var(--darkColor);
	height: calc(100vh / var(--zoom));
	max-height: 900px;
	width: 100%;
	overflow: hidden;
	position: relative;
	z-index: 0;
	box-sizing: border-box;

	@media screen and (min-width: 768px) {
		min-height: 650px;
	}

	picture {
		--brightness: 0.35;
		position: absolute;
		left: 0;
		right: 0;
		bottom: 0;
		top: 0;
		display: block;
		z-index: -1;
		filter: brightness(var(--brightness));

		img {
			--scale: 1;
			display: block;
			width: 100%;
			/* min-height: 95vh; */
			height: 100%;
			object-fit: cover;
			transform: scale(var(--scale));
			object-position: var(--moveX) 0;

			@media screen and (min-width: 768px) {
				transform: scale(1);
				object-position: 50% 15%;
				object-fit: cover;
			}
		}
	}

	.texts {
		z-index: 5;
		display: grid;
		grid-template-rows: 1.14fr 0.7fr 0.4fr 0.38fr 0.52fr;
		grid-template-columns: 1fr;
		gap: 0;
		height: 95%;
		color: white;
		max-width: 1200px;
		margin: 0 auto 0;
		padding: 0 1rem;
		box-sizing: border-box;
		font-family: var(--font2);
		width: 100%;

		@media screen and (min-width: 768px) {
			width: calc(100% - 2rem);
			padding: 0;
		}

		.first {
			font-family: var(--font1);
			align-self: end;
			font-size: clamp(2.5rem, 11vw, 4.5rem);
			line-height: 0.8;
			letter-spacing: -0.1rem;
			color: hsl(201deg 7.03% 73.77%);

			@media screen and (min-width: 768px) {
				font-size: 4.5rem;
			}
		}

		.second {
			--length: 5;
			overflow: hidden;
			font-size: clamp(2.7rem, calc(180vw / var(--length)), 8rem);
			letter-spacing: calc(-2.8rem / var(--length));
			line-height: 1;
			transform: translateX(-0.2rem);

			@media screen and (min-width: 768px) {
				transform: translateX(-0.5rem);
				letter-spacing: -0.55rem;
				font-size: 8rem;
			}
		}

		.buttonHero {
			display: flex;
			align-items: center;

			a {
				font-family: var(--font1);
				padding: 1rem 3rem;
				color: var(--accentColor);
				background-color: transparent;
				border-radius: 30px;
				transition: all 0.2s ease-in-out;
				border: 2px solid var(--accentColor);

				span {
					transform: scale(1);
					transition: all 0.2s ease-in-out;
					transform-origin: center;
					display: inline-block;
				}

				@media (hover: hover) and (pointer: fine) {
					&:hover {
						background-color: var(--darkColor);

						span {
							transform: scale(1.1);
						}
					}
				}
			}
		}

		.premenu {
			color: var(--accentDarkerColor);
			font-family: var(--font1);
			line-height: 1;
			padding-bottom: 0;
			letter-spacing: -0.2rem;
			font-size: clamp(2.2rem, 10vw, 4.5rem);

			@media screen and (min-width: 768px) {
				padding-bottom: 1rem;
				letter-spacing: -0.2rem;
				font-size: 4.5rem;
			}
		}

		.menu {
			font-family: var(--font1);
			position: relative;
			border-top: 2px solid white;
			display: flex;
			flex-direction: row;
			flex-wrap: wrap;
			gap: 4%;

			@media screen and (min-width: 768px) {
				display: grid;
				grid-auto-flow: column;
				grid-template-columns: 1fr 1fr 0.8fr 1.2fr 1fr;
				grid-template-rows: 1fr;
				gap: 0;
			}

			a {
				width: 48%;
				color: rgba(255, 255, 255, 0.74);
				padding: 1rem 0 0;
				font-size: 1.2rem;
				line-height: 0.95;
				font-weight: 300;
				transform-origin: top center;
				transition: all 0.2s ease-in-out;

				@media screen and (min-width: 768px) {
					position: relative;
					color: rgba(255, 255, 255, 0.5);
					width: 100%;
					font-size: clamp(1.4rem, 3vw, 1.65rem);
					line-height: 0.85;
					padding-right: 1rem;
				}

				@media (hover: hover) and (pointer: fine) {
					&:hover {
						color: white;
					}
				}

				&.active,
				&.active:hover {
					color: var(--accentColor);

					@media screen and (min-width: 768px) {
						color: white;
					}

					&:before {
						content: '';
						position: absolute;
						top: -3px;
						left: 0;
						display: block;
						background-color: var(--accentColor);
						height: 4px;
						right: 0;

						@media screen and (min-width: 768px) {
							right: 7%;
						}
					}
				}
			}
		}
	}
}
